        case 8:

                conversion.ADC_Word = 1023;

                break;
        case 16:
            if(vehicle.speed >= 5 ){
                conversion.ADC_Word = 409;
            }else{
                conversion.ADC_Word = 1023;
            }
                break;
        case 32:

                conversion.ADC_Word = 818;
                
