void GoToSleep(void){
    //add lines if necessary
    //RCONbits.SLEEP = 0;
    RCONbits.IDLE = 0;
    RCONbits.WDTO = 0;
    //Disable DMA interrupts
    IPC1bits.DMA0IP = 0;
    IPC6bits.DMA2IP = 0;
    //ADC disabled in idle ADSIDL = 1
    Idle();   
}

void ReturnFromSleep(void){
    if(RCONbits.IDLE == 1){
        RCONbits.IDLE = 0;
        if(RCONbits.WDTO == 1){
            RCONbits.WDTO = 0;
        }
        ClrWdt();
        IPC1bits.DMA0IP = 1;
        IPC6bits.DMA2IP = 1;
    }
    //add lines if necessary
}

void WiperActions(void){
    #ifndef WIPER_CODE
    
    switch(wiper_state){
        case 0: //STBY
            if(VcuState_A.WIPER == 1){
                wiper_state = 1;
            }
            break;
        case 1: //READY
            //Enable Converter
            IO_WIPER_CONV_EN_SetHigh();
            //Start PWM module
            PTCON = 0x8000;
            //Start Timer
            flags.wiper_on = true;
            if(flags.wiper_move == true)
            {
                wiper_state = 2;
                flags.wiper_move = false;
            }
            break;
        case 2: // Right
            PDC2 = WIPER_LIMIT_RIGHT;
            if(VcuState_A.WIPER == 0)
            {
                wiper_state = 4;
            }
            if(flags.wiper_move == true)
            {
                wiper_state = 3;
                flags.wiper_move = false;
            }
            break;
        case 3: // Left
            PDC2 = WIPER_LIMIT_LEFT;
            if(VcuState_A.WIPER == 0)
            {
                wiper_state = 4;
            }
            if(flags.wiper_move == true)
            {
                wiper_state = 2;
                flags.wiper_move = false;
            }
            break; 
        case 4: // Center and ready to SW OFF
            if(flags.wiper_move == true)
            {
                PDC2 = WIPER_WINDSCREEN_CENTER;
                flags.wiper_move = false;
                wiper_state = 5;
            }
        case 5: // ALL OFF
            if(flags.wiper_move == true){
                //Turn OFF PWM Module
                PTCON = 0x0000;
                //Disable Wiper Timer Counter
                flags.wiper_on = false;
                flags.wiper_move = false;
                //Disable 5V Converter
                IO_WIPER_CONV_EN_SetLow();
                wiper_state = 0;
            }
            break;
        default:
            break;
        }
        #else
            switch(wiper_state){
             case 0: //STBY
                if(VcuState_A.WIPER == 1){
                    wiper_state = 1;
                }
                break;
            case 1: //READY
                //Enable Converter
                IO_WIPER_CONV_EN_SetHigh();
                //Start PWM module
                PTCON = 0x8000;
                //Start Timer
                flags.wiper_on = true;
                if(flags.wiper_move == true)
                {
                    wiper_state = 2;
                    flags.wiper_move = false;
                }
                break;
            case 2: // Left
                PDC2 = WIPER_LEFT_X[wiper_step_cnt];
                if(VcuState_A.WIPER == 0)
                {
                wiper_state = 4;
                }
                if(wiper_step_cnt >= 79){
                    wiper_state = 3;
                    flags.wiper_move = false;
                }
                break; 
            case 3: // Right
                
            PDC2 = WIPER_LEFT_X[wiper_step_cnt];
                if(VcuState_A.WIPER == 0)
                {
                wiper_state = 4;
                }
                if(wiper_step_cnt <= 0){
                    wiper_state = 2;
                    flags.wiper_move = false;
                }
                break;
            case 4: // Center and ready to SW OFF
            if(flags.wiper_move == true)
                {
                PDC2 = WIPER_WINDSCREEN_CENTER;
                wiper_step_cnt = 0;
                flags.wiper_move = false;
                wiper_state = 5;
                }
                break;
            case 5: // ALL OFF
            if(flags.wiper_move == true){
                //Turn OFF PWM Module
                PTCON = 0x0000;
                //Disable Wiper Timer Counter
                flags.wiper_on = false;
                flags.wiper_move = false;
                //Disable 5V Converter
                IO_WIPER_CONV_EN_SetLow();
                wiper_state = 0;
                }
                break;
            default:
                break;
            }
                
        #endif
    
}

